// ===== ROUTER + VALIDACIÃ“N COMBINADO =====
const message = $input.item.json.message;
const text = message.text || '';
const chatId = message.chat.id;
const username = message.from.username || 'usuario';

// Detectar comando
if (text === '/help') {
  return [
    {
      json: {
        command: 'help',
        chatId: chatId,
        message: 'ðŸ¤– *EduRAG Bot - IA de CalificaciÃ³n*\n\n' +
