  }
];

// ===== FORMATEAR LISTA + CONSTRUIR RESPUESTA =====
const chatId = $('Smart Router').first().json.chatId;

// La respuesta del HTTP viene como array de items
// Cada item tiene un .json con los datos del material
const items = $input.all();

if (!items || items.length === 0) {
  return {
    json: {
      chatId: chatId,
      message: 'âœ… No hay materiales pendientes de calificar.'
    }
  };
}

let msg = 'ðŸ“š *Materiales Pendientes*\n\n';
items.forEach((item, i) => {
  const m = item.json;
  msg += `${i+1}. *${m.title}*\n`;
